import {Component, Input} from '@angular/core';

@Component({
  selector: 'app-hero',
  templateUrl: './hero.component.html'
})
export class HeroComponent {
  @Input() fields;

  constructor() {
  }

}
<section [id]="fields.scroll_anchor_id" class="hero-section">
  <div class="container">
    <div class="row align-items-center">
      <div class="col-xl-6 col-lg-6 col-md-10">
        <div class="hero-content">
          <h1>{{fields.headline}}</h1>
          <p>{{fields.subheadline}}</p>

          <a [href]="fields.button_url" class="main-btn btn-hover" target="_blank">{{fields.button_label}}</a>
          <a href="https://buttercms.com/join/" target="_blank">Need an account?</a>
        </div>
      </div>
      <div class="col-xxl-6 col-xl-6 col-lg-6">
        <div class="hero-image text-center text-lg-end">
          <img [src]="fields.image" alt="">
        </div>
      </div>
    </div>
  </div>
</section>
